mapper.select:
select *
from(
select rownum rnum, n1.*
from(
select * from
tb_board
order by ${orderby} asc
)n1
)
where b_num like 1 and rnum between #{page.start} and #{page.end}

select *
from(
select rownum rnum, n1.*
from(
select * from
tb_board
order by ${orderby} asc
)n1
)
where b_num like 0 and rnum between #{page.start} and #{page.end}
